command:
//This macro randomly selects an arc with weapons in, for use when a critical damage effect is applied to a weapon system.
if (canvas.tokens.controlled.length <= 0) {
    ui.notifications.error(game.i18n.localize("SFRPG.StarshipSheet.RandomQuadrantMacro.NoToken"));
    return;
};

if (canvas.tokens.controlled[0].actor.type !== "starship") {
    ui.notifications.error(game.i18n.localize("SFRPG.StarshipSheet.RandomQuadrantMacro.NotStarship"));
    return;
};

const weaponArrays = {
    forwardWeapons: canvas.tokens.controlled[0].actor.items.filter(i => i.data.data.mount?.arc === "forward"),
    portWeapons: canvas.tokens.controlled[0].actor.items.filter(i => i.data.data.mount?.arc === "port"),
    starboardWeapons: canvas.tokens.controlled[0].actor.items.filter(i => i.data.data.mount?.arc === "starboard"),
    aftWeapons: canvas.tokens.controlled[0].actor.items.filter(i => i.data.data.mount?.arc === "aft")
};

for (const arr of Object.entries(weaponArrays)) {
    if (arr[1].length < 1) {
        delete weaponArrays[arr[0]];
    }
};

const entries = Object.entries(weaponArrays);

const results = entries.map((e, index, arr) => {
    let arc = e[0].replace(/Weapons/g, "");
    arc = arc.charAt(0).toUpperCase() + arc.slice(1);
    const slug = `SFRPG.Rolls.StarshipActions.Quadrant.${arc}`

    return {
        text: game.i18n.format(slug),
        type: CONST.TABLE_RESULT_TYPES.TEXT,
        img: "icons/svg/target.svg",
        weight: 1,
        range: [index+1, index+1],
        drawn: false
    }
});

table = new RollTable({
    name: "Random Weapon Arc",
    results: results,
    formula: `1d${entries.length}`,
    description: "Select a random weapon arc to apply the critical damage condition to."
});

table.draw();
